   464‚Üí	function add_product_to_cart() {
   465‚Üí		// Esegui solo se non √® stato gi√† aggiunto in questa sessione
   466‚Üí		if (!WC()->session->get('magazine_added') && !is_admin() && (is_cart() || is_checkout())) {
   467‚Üí			global $woocommerce;
   468‚Üí			$old_product_id = 317501; // AK magazine v4 Old variation product ID
   469‚Üí			$product_id = 321608; // AK magazine v5 Stylia variation product ID
   470‚Üí			$gift_card_id = 306636; // AK gift card product ID
   471‚Üí			$cart_total = 1; // cart must not be empty
   472‚Üí			
   473‚Üí			if ($woocommerce->cart->total >= $cart_total) {
   474‚Üí				// Check if any product already in cart
   475‚Üí				if (sizeof($woocommerce->cart->get_cart()) > 0) {
   476‚Üí					$is_valid_cart = true;
   477‚Üí					$magazine_already_in_cart = false;
   478‚Üí					
   479‚Üí					foreach ($woocommerce->cart->get_cart() as $cart_item_key => $values) {
   480‚Üí						$_product = $values['data'];
   481‚Üí						
   482‚Üí						if ($_product->get_id() == $old_product_id) {
   483‚Üí							$woocommerce->cart->remove_cart_item($cart_item_key);
   484‚Üí						}
   485‚Üí						
   486‚Üí						// Check if the new magazine is already in cart
   487‚Üí						if ($_product->get_id() == $product_id) {
   488‚Üí							$magazine_already_in_cart = true;
   489‚Üí						}
   490‚Üí						
   491‚Üí						// If there is a product other than the Gift card or Bag preorder, set the flag to false
   492‚Üí						if ($_product->get_id() != $gift_card_id && !has_term('preorder', 'product_tag', $_product->get_id())) {
   493‚Üí							$is_valid_cart = false;
   494‚Üí						}
   495‚Üí					}
   496‚Üí					
   497‚Üí					// If the cart only contains products with ID 306636 or that have product_tag ID 403, DO NOT add the Magazine
   498‚Üí					if ($is_valid_cart) {
   499‚Üí						// Don't add magazine
   500‚Üí					} else if (!$magazine_already_in_cart) {
   501‚Üí						// Only add the magazine if it's not already in cart
   502‚Üí						$woocommerce->cart->add_to_cart($product_id);
   503‚Üí						WC()->session->set('magazine_added', true);
   504‚Üí					}
   505‚Üí				} else {
   506‚Üí					// If no products in cart, add the Magazine
   507‚Üí					$woocommerce->cart->add_to_cart($product_id);
   508‚Üí					WC()->session->set('magazine_added', true);
   509‚Üí				}
   510‚Üí			}
   511‚Üí		}
   512‚Üí	}
