   516‚Üí	function modify_product_price_zero_if_cart_total( $cart ) {
   517‚Üí		// This is necessary for WC 3.0+
   518‚Üí		if ( is_admin() && ! defined( 'DOING_AJAX' ) ) {
   519‚Üí			return;
   520‚Üí		}
   521‚Üí		
   522‚Üí		// Evita calcoli ricorsivi
   523‚Üí		if ( did_action( 'woocommerce_before_calculate_totals' ) >= 2 ) {
   524‚Üí			return;
   525‚Üí		}
   526‚Üí		
   527‚Üí		$product_id = 321607; //AK Magazine v5 Stylia
