   533‚Üí			if ( is_object( $cart_item['data'] ) && method_exists( $cart_item['data'], 'get_price' ) ) {
   534‚Üí				$item_price = $cart_item['data']->get_price();
   535‚Üí				if ( is_numeric( $item_price ) ) {
   536‚Üí					$subtotal += floatval( $item_price );
   537‚Üí				}
   538‚Üí			}
   539‚Üí		}
   540‚Üí		
   541‚Üí		if ( $subtotal > $price ) {
   542‚Üí			foreach ( $cart->get_cart() as $cart_item_key => $cart_item ) {
   543‚Üí				if ( isset( $cart_item['product_id'] ) && $cart_item['product_id'] == $product_id ) {
   544‚Üí					if ( is_object( $cart_item['data'] ) && method_exists( $cart_item['data'], 'set_price' ) ) {
   545‚Üí						$cart_item['data']->set_price( 0 );
   546‚Üí					}
   547‚Üí				}
   548‚Üí			}
   549‚Üí		}
   550‚Üí	}
