   718‚Üí	function wc_auto_generate_variations_skus( $product ) {
   719‚Üí		if( $product->is_type('variable') && isset($_POST['generate_variations_skus']) ) {
   720‚Üí			$parent_sku   = $product->get_sku();
   721‚Üí			$children_ids = $product->get_children();
   722‚Üí			$count        = 0;
   723‚Üí			
   724‚Üí			// Loop through the variations Ids
   725‚Üí			foreach( $children_ids as $child_id ){
   726‚Üí				$count++;
   727‚Üí				
   728‚Üí				// Get an instance of the WC_Product_Variation object
   729‚Üí				$variation = wc_get_product( $child_id );
   730‚Üí				
   731‚Üí				// Set the prefix length based on variations count
   732‚Üí				//$prefix = sizeof($children_ids) < 100 ? sprintf('%02d', $count) : sprintf('%03d', $count);
   733‚Üí				
   734‚Üí				$prefix = str_replace( ',' , '_', $variation->get_attribute( 'pa_size' ) );
   735‚Üí				
   736‚Üí				// Generate and set the sku
   737‚Üí				$variation->set_sku( $parent_sku . '-' . $prefix );
   738‚Üí				
   739‚Üí				// Save variation
   740‚Üí				$variation->save();
   741‚Üí			}
   742‚Üí		}
   743‚Üí	}
