   754‚Üí	add_action( 'woocommerce_sale_flash', 'sale_badge_percentage', 25 );
   755‚Üí	function sale_badge_percentage() {
   756‚Üí		global $product;
   757‚Üí		if ( ! $product->is_on_sale() ) return;
   758‚Üí		if ( $product->is_type( 'simple' ) ) {
   759‚Üí			$max_percentage = ( ( $product->get_regular_price() - $product->get_sale_price() ) / $product->get_regular_price() ) * 100;
   760‚Üí		} elseif ( $product->is_type( 'variable' ) ) {
   761‚Üí			$max_percentage = 0;
   762‚Üí			foreach ( $product->get_children() as $child_id ) {
   763‚Üí				$variation = wc_get_product( $child_id );
   764‚Üí				$price = $variation->get_regular_price();
   765‚Üí				$sale = $variation->get_sale_price();
   766‚Üí				if ( $price != 0 && ! empty( $sale ) ) $percentage = ( $price - $sale ) / $price * 100;
   767‚Üí				if ( $percentage > $max_percentage ) {
   768‚Üí					$max_percentage = $percentage;
   769‚Üí				}
   770‚Üí			}
   771‚Üí		}
   772‚Üí		
   773‚Üí		$product_id = $product->get_id();
   774‚Üí		if ( has_term( 379, 'product_tag', $product_id ) && ! has_term( 387, 'product_tag', $product_id ) ) {
   775‚Üí			$sale_text = __( 'Sale', 'kraft' );
   776‚Üí		} else {
   777‚Üí			date_default_timezone_set( 'Europe/Paris' ); // Set the timezone to CET
   778‚Üí			
   779‚Üí			$isDaylightSaving = date( 'I' ); // 1 if in summer time, 0 if in winter time
   780‚Üí			
   781‚Üí			if ( $isDaylightSaving ) {
   782‚Üí				$sale_text = __( 'Summer sale', 'kraft' );
   783‚Üí			} else {
   784‚Üí				$current_date = date( 'Ymd' );
   785‚Üí				if ( $current_date >= 20241129 && $current_date <= 20241220 ) {
   786‚Üí					$sale_text = __( 'Black Friday', 'kraft' );
   787‚Üí				}
   788‚Üí        elseif ( $current_date >= 20231201 && $current_date <= 20240106 ) {
   789‚Üí					$sale_text = __( 'XMAS sale', 'kraft' );
   790‚Üí				}
   791‚Üí				else {
   792‚Üí					$sale_text = __( 'Winter sale', 'kraft' );
   793‚Üí				}
   794‚Üí			}
   795‚Üí		}
   796‚Üí		
   797‚Üí		if ( $max_percentage > 0 ) echo "<span class='onsale'>" . $sale_text . " -" . round($max_percentage) . "%</span>"; // If you would like to show -40% off then add text after % sign
   798‚Üí	}
