   800‚Üí	// Display the Woocommerce Discount Percentage on the Sale Badge for variable products and single products
   801‚Üí	add_filter( 'woocommerce_sale_flash', 'display_percentage_on_sale_badge', 20, 3 );
   802‚Üí	function display_percentage_on_sale_badge( $html, $post, $product ) {
   803‚Üí		if ( $product->is_type( 'variable' ) ) {
   804‚Üí			$percentages = array();
   805‚Üí			
   806‚Üí			// This will get all the variation prices and loop throughout them
   807‚Üí			$prices = $product->get_variation_prices();
   808‚Üí			
   809‚Üí			foreach( $prices['price'] as $key => $price ){
   810‚Üí				// Only on sale variations
   811‚Üí				if( $prices['regular_price'][$key] !== $price ){
   812‚Üí					// Calculate and set in the array the percentage for each variation on sale
   813‚Üí					$percentages[] = round( 100 - ( floatval( $prices['sale_price'][$key]) / floatval($prices['regular_price'][$key] ) * 100 ) );
   814‚Üí				}
   815‚Üí			}
   816‚Üí			// Displays maximum discount value
   817‚Üí			$percentage = max($percentages) . '%';
   818‚Üí			
   819‚Üí		} elseif( $product->is_type( 'grouped' ) ) {
   820‚Üí			$percentages = array();
   821‚Üí			
   822‚Üí			// This will get all the variation prices and loop throughout them
   823‚Üí			$children_ids = $product->get_children();
   824‚Üí			
   825‚Üí			foreach( $children_ids as $child_id ) {
   826‚Üí				$child_product = wc_get_product( $child_id );
   827‚Üí				
   828‚Üí				$regular_price = (float) $child_product->get_regular_price();
   829‚Üí				$sale_price    = (float) $child_product->get_sale_price();
   830‚Üí				
   831‚Üí				if ( $sale_price != 0 || ! empty($sale_price) ) {
   832‚Üí					// Calculate and set in the array the percentage for each child on sale
   833‚Üí					$percentages[] = round(100 - ($sale_price / $regular_price * 100));
   834‚Üí				}
   835‚Üí			}
   836‚Üí			// Displays maximum discount value
   837‚Üí			$percentage = max( $percentages ) . '%';
   838‚Üí			
   839‚Üí		} else {
   840‚Üí			$regular_price = (float) $product->get_regular_price();
   841‚Üí			$sale_price    = (float) $product->get_sale_price();
   842‚Üí			
   843‚Üí			if ( $sale_price != 0 || ! empty( $sale_price ) ) {
   844‚Üí				$percentage    = round( 100 - ( $sale_price / $regular_price * 100 ) ) . '%';
   845‚Üí			} else {
   846‚Üí				return $html;
