   955‚Üí	 *
   956‚Üí	 * @return array
   957‚Üí	 */
   958‚Üí	function my_hide_shipping_when_free_is_available( array $rates ) {
   959‚Üí		$free = array();
   960‚Üí		foreach ( $rates as $rate_id => $rate ) {
   961‚Üí			if ( 'free_shipping' === $rate->method_id ) {
   962‚Üí				$free[ $rate_id ] = $rate;
   963‚Üí				break;
   964‚Üí			}
   965‚Üí		}
   966‚Üí		return ! empty( $free ) ? $free : $rates;
   967‚Üí	}
