  1091‚Üí	/**
  1092‚Üí	 * Apply a WooCommerce discount code to cart via URL parameter.
  1093‚Üí	 * This code is adapted from: https://www.webroomtech.com/apply-coupon-via-url-in-woocommerce/
  1094‚Üí	 */
  1095‚Üí	function my_woocommerce_apply_cart_coupon_in_url() {
  1096‚Üí		// Return early if WooCommerce or sessions aren't available.
  1097‚Üí		if ( ! function_exists( 'WC' ) || ! WC()->session ) {
  1098‚Üí			return;
  1099‚Üí		}
  1100‚Üí		
  1101‚Üí		// Return if there is no coupon in the URL, otherwise set the variable.
  1102‚Üí		if ( empty( $_REQUEST['coupon'] ) ) {
  1103‚Üí			return;
  1104‚Üí		} else {
  1105‚Üí			$coupon_code = esc_attr( $_REQUEST['coupon'] );
  1106‚Üí		}
  1107‚Üí		
  1108‚Üí		// Set a session cookie to remember the coupon if they continue shopping.
  1109‚Üí		WC()->session->set_customer_session_cookie(true);
  1110‚Üí		
  1111‚Üí		// Apply the coupon to the cart if necessary.
  1112‚Üí		if ( ! WC()->cart->has_discount( $coupon_code ) ) {
  1113‚Üí			
  1114‚Üí			// WC_Cart::add_discount() sanitizes the coupon code.
  1115‚Üí			WC()->cart->add_discount( $coupon_code );
  1116‚Üí		}
  1117‚Üí	}
  1118‚Üí	add_action('wp_loaded', 'my_woocommerce_apply_cart_coupon_in_url', 30);
  1119‚Üí	add_action('woocommerce_add_to_cart', 'my_woocommerce_apply_cart_coupon_in_url');
