   464→	function add_product_to_cart() {
   465→		// Esegui solo se non è stato già aggiunto in questa sessione
   466→		if (!WC()->session->get('magazine_added') && !is_admin() && (is_cart() || is_checkout())) {
   467→			global $woocommerce;
   468→			$old_product_id = 317501; // AK magazine v4 Old variation product ID
   469→			$product_id = 321608; // AK magazine v5 Stylia variation product ID
   470→			$gift_card_id = 306636; // AK gift card product ID
   471→			$cart_total = 1; // cart must not be empty
   472→			
   473→			if ($woocommerce->cart->total >= $cart_total) {
   474→				// Check if any product already in cart
   475→				if (sizeof($woocommerce->cart->get_cart()) > 0) {
   476→					$is_valid_cart = true;
   477→					$magazine_already_in_cart = false;
   478→					
   479→					foreach ($woocommerce->cart->get_cart() as $cart_item_key => $values) {
   480→						$_product = $values['data'];
   481→						
   482→						if ($_product->get_id() == $old_product_id) {
   483→							$woocommerce->cart->remove_cart_item($cart_item_key);
   484→						}
   485→						
   486→						// Check if the new magazine is already in cart
   487→						if ($_product->get_id() == $product_id) {
   488→							$magazine_already_in_cart = true;
   489→						}
   490→						
   491→						// If there is a product other than the Gift card or Bag preorder, set the flag to false
   492→						if ($_product->get_id() != $gift_card_id && !has_term('preorder', 'product_tag', $_product->get_id())) {
   493→							$is_valid_cart = false;
   494→						}
   495→					}
   496→					
   497→					// If the cart only contains products with ID 306636 or that have product_tag ID 403, DO NOT add the Magazine
   498→					if ($is_valid_cart) {
   499→						// Don't add magazine
   500→					} else if (!$magazine_already_in_cart) {
   501→						// Only add the magazine if it's not already in cart
   502→						$woocommerce->cart->add_to_cart($product_id);
   503→						WC()->session->set('magazine_added', true);
   504→					}
   505→				} else {
   506→					// If no products in cart, add the Magazine
   507→					$woocommerce->cart->add_to_cart($product_id);
   508→					WC()->session->set('magazine_added', true);
   509→				}
   510→			}
   511→		}
   512→	}
