   515→	add_action( 'woocommerce_before_calculate_totals', 'modify_product_price_zero_if_cart_total', 99 );
   516→	function modify_product_price_zero_if_cart_total( $cart ) {
   517→		// This is necessary for WC 3.0+
   518→		if ( is_admin() && ! defined( 'DOING_AJAX' ) ) {
   519→			return;
   520→		}
   521→		
   522→		// Evita calcoli ricorsivi
   523→		if ( did_action( 'woocommerce_before_calculate_totals' ) >= 2 ) {
   524→			return;
   525→		}
   526→		
   527→		$product_id = 321607; //AK Magazine v5 Stylia
   528→		$price      = 20;
   529→		
   530→		$subtotal = 0;
   531→		foreach ( $cart->get_cart() as $cart_item_key => $cart_item ) {
   532→			// Verifica che l'oggetto esista e che abbia il metodo get_price
   533→			if ( is_object( $cart_item['data'] ) && method_exists( $cart_item['data'], 'get_price' ) ) {
   534→				$item_price = $cart_item['data']->get_price();
   535→				if ( is_numeric( $item_price ) ) {
   536→					$subtotal += floatval( $item_price );
   537→				}
   538→			}
   539→		}
   540→		
   541→		if ( $subtotal > $price ) {
   542→			foreach ( $cart->get_cart() as $cart_item_key => $cart_item ) {
   543→				if ( isset( $cart_item['product_id'] ) && $cart_item['product_id'] == $product_id ) {
   544→					if ( is_object( $cart_item['data'] ) && method_exists( $cart_item['data'], 'set_price' ) ) {
   545→						$cart_item['data']->set_price( 0 );
   546→					}
   547→				}
   548→			}
   549→		}
   550→	}
