   717→	//add_action( 'woocommerce_admin_process_product_object', 'wc_auto_generate_variations_skus', 10, 1 );
   718→	function wc_auto_generate_variations_skus( $product ) {
   719→		if( $product->is_type('variable') && isset($_POST['generate_variations_skus']) ) {
   720→			$parent_sku   = $product->get_sku();
   721→			$children_ids = $product->get_children();
   722→			$count        = 0;
   723→			
   724→			// Loop through the variations Ids
   725→			foreach( $children_ids as $child_id ){
   726→				$count++;
   727→				
   728→				// Get an instance of the WC_Product_Variation object
   729→				$variation = wc_get_product( $child_id );
   730→				
   731→				// Set the prefix length based on variations count
   732→				//$prefix = sizeof($children_ids) < 100 ? sprintf('%02d', $count) : sprintf('%03d', $count);
   733→				
   734→				$prefix = str_replace( ',' , '_', $variation->get_attribute( 'pa_size' ) );
   735→				
   736→				// Generate and set the sku
   737→				$variation->set_sku( $parent_sku . '-' . $prefix );
   738→				
   739→				// Save variation
   740→				$variation->save();
   741→			}
   742→		}
   743→	}
