   755→	function sale_badge_percentage() {
   756→		global $product;
   757→		if ( ! $product->is_on_sale() ) return;
   758→		if ( $product->is_type( 'simple' ) ) {
   759→			$max_percentage = ( ( $product->get_regular_price() - $product->get_sale_price() ) / $product->get_regular_price() ) * 100;
   760→		} elseif ( $product->is_type( 'variable' ) ) {
   761→			$max_percentage = 0;
   762→			foreach ( $product->get_children() as $child_id ) {
   763→				$variation = wc_get_product( $child_id );
   764→				$price = $variation->get_regular_price();
   765→				$sale = $variation->get_sale_price();
   766→				if ( $price != 0 && ! empty( $sale ) ) $percentage = ( $price - $sale ) / $price * 100;
   767→				if ( $percentage > $max_percentage ) {
   768→					$max_percentage = $percentage;
   769→				}
   770→			}
   771→		}
   772→		
   773→		$product_id = $product->get_id();
   774→		if ( has_term( 379, 'product_tag', $product_id ) && ! has_term( 387, 'product_tag', $product_id ) ) {
   775→			$sale_text = __( 'Sale', 'kraft' );
   776→		} else {
   777→			date_default_timezone_set( 'Europe/Paris' ); // Set the timezone to CET
   778→			
   779→			$isDaylightSaving = date( 'I' ); // 1 if in summer time, 0 if in winter time
   780→			
   781→			if ( $isDaylightSaving ) {
   782→				$sale_text = __( 'Summer sale', 'kraft' );
   783→			} else {
   784→				$current_date = date( 'Ymd' );
   785→				if ( $current_date >= 20241129 && $current_date <= 20241220 ) {
   786→					$sale_text = __( 'Black Friday', 'kraft' );
   787→				}
   788→        elseif ( $current_date >= 20231201 && $current_date <= 20240106 ) {
   789→					$sale_text = __( 'XMAS sale', 'kraft' );
   790→				}
   791→				else {
   792→					$sale_text = __( 'Winter sale', 'kraft' );
   793→				}
   794→			}
   795→		}
   796→		
   797→		if ( $max_percentage > 0 ) echo "<span class='onsale'>" . $sale_text . " -" . round($max_percentage) . "%</span>"; // If you would like to show -40% off then add text after % sign
   798→	}
