   800→	// Display the Woocommerce Discount Percentage on the Sale Badge for variable products and single products
   801→	add_filter( 'woocommerce_sale_flash', 'display_percentage_on_sale_badge', 20, 3 );
   802→	function display_percentage_on_sale_badge( $html, $post, $product ) {
   803→		if ( $product->is_type( 'variable' ) ) {
   804→			$percentages = array();
   805→			
   806→			// This will get all the variation prices and loop throughout them
   807→			$prices = $product->get_variation_prices();
   808→			
   809→			foreach( $prices['price'] as $key => $price ){
   810→				// Only on sale variations
   811→				if( $prices['regular_price'][$key] !== $price ){
   812→					// Calculate and set in the array the percentage for each variation on sale
   813→					$percentages[] = round( 100 - ( floatval( $prices['sale_price'][$key]) / floatval($prices['regular_price'][$key] ) * 100 ) );
   814→				}
   815→			}
   816→			// Displays maximum discount value
   817→			$percentage = max($percentages) . '%';
   818→			
   819→		} elseif( $product->is_type( 'grouped' ) ) {
   820→			$percentages = array();
   821→			
   822→			// This will get all the variation prices and loop throughout them
   823→			$children_ids = $product->get_children();
   824→			
   825→			foreach( $children_ids as $child_id ) {
   826→				$child_product = wc_get_product( $child_id );
   827→				
   828→				$regular_price = (float) $child_product->get_regular_price();
   829→				$sale_price    = (float) $child_product->get_sale_price();
   830→				
   831→				if ( $sale_price != 0 || ! empty($sale_price) ) {
   832→					// Calculate and set in the array the percentage for each child on sale
   833→					$percentages[] = round(100 - ($sale_price / $regular_price * 100));
   834→				}
   835→			}
   836→			// Displays maximum discount value
   837→			$percentage = max( $percentages ) . '%';
   838→			
   839→		} else {
   840→			$regular_price = (float) $product->get_regular_price();
   841→			$sale_price    = (float) $product->get_sale_price();
   842→			
   843→			if ( $sale_price != 0 || ! empty( $sale_price ) ) {
   844→				$percentage    = round( 100 - ( $sale_price / $regular_price * 100 ) ) . '%';
   845→			} else {
   846→				return $html;
   847→			}
   848→		}
   849→		return '<span class="onsale">' . esc_html__( 'up to -', 'woocommerce' ) . ' '. $percentage . '</span>'; // If needed then change or remove "up to -" text
   850→	}
