  1091→	/**
  1092→	 * Apply a WooCommerce discount code to cart via URL parameter.
  1093→	 * This code is adapted from: https://www.webroomtech.com/apply-coupon-via-url-in-woocommerce/
  1094→	 */
  1095→	function my_woocommerce_apply_cart_coupon_in_url() {
  1096→		// Return early if WooCommerce or sessions aren't available.
  1097→		if ( ! function_exists( 'WC' ) || ! WC()->session ) {
  1098→			return;
  1099→		}
  1100→		
  1101→		// Return if there is no coupon in the URL, otherwise set the variable.
  1102→		if ( empty( $_REQUEST['coupon'] ) ) {
  1103→			return;
  1104→		} else {
  1105→			$coupon_code = esc_attr( $_REQUEST['coupon'] );
  1106→		}
  1107→		
  1108→		// Set a session cookie to remember the coupon if they continue shopping.
  1109→		WC()->session->set_customer_session_cookie(true);
  1110→		
  1111→		// Apply the coupon to the cart if necessary.
  1112→		if ( ! WC()->cart->has_discount( $coupon_code ) ) {
  1113→			
  1114→			// WC_Cart::add_discount() sanitizes the coupon code.
  1115→			WC()->cart->add_discount( $coupon_code );
  1116→		}
  1117→	}
  1118→	add_action('wp_loaded', 'my_woocommerce_apply_cart_coupon_in_url', 30);
  1119→	add_action('woocommerce_add_to_cart', 'my_woocommerce_apply_cart_coupon_in_url');
