   104â†’	function render_ak_home_composite( $atts ) {
   105â†’		global $post;
   106â†’		
   107â†’		// Recupera gli elementi
   108â†’		$items = get_field( 'grid-items', $post );
   109â†’		
   110â†’		// Assicurati che $items sia un array
   111â†’		if ( ! is_array( $items ) ) {
   112â†’			$items = [];
   113â†’		}
   114â†’		
   115â†’		$output = '';
   116â†’		$i = 0;
   117â†’		$count = count( $items ); // Ora sicuro perchÃ© $items Ã¨ sempre un array
   118â†’		ob_start();
   119â†’		?>
   120â†’      <div class="grid composite">
   121â†’				<?php foreach ( $items as $item ) { ?>
   122â†’					<?= $i % 3 === 0 ? '<div class="col">' : ''; ?>
   123â†’            
   124â†’            <div class="item" <?= 'style="aspect-ratio: ' . $item['image']['width'] . ' / ' .  $item['image']['height'] . '"'; ?>>
   125â†’							<?php
   126â†’								if ( $item['shortcode'] ) {
   127â†’									echo do_shortcode( $item['shortcode'] );
   128â†’								} else {
   129â†’									$target = $item['external_link'] ? 'target="_blank"' : '';
   130â†’									echo $item['link'] ? '<a href="' . $item['link'] . '" ' . $target . ' style="display: block; aspect-ratio: ' . $item['image']['width'] . ' / ' .  $item['image']['height'] . '">' : '';
   131â†’									if ( $i === 2 || $i === 3 || $i === 8 ) {
   132â†’										$size = [726, 906];
   133â†’									} else {
   134â†’										$size = [352, 440];
   135â†’									}
   136â†’									echo wp_get_attachment_image(
   137â†’										$item['image']['ID'],
   138â†’										$size,
   139â†’										false,
   140â†’										[
   141â†’											'loading' => 'lazy',
   142â†’											'decoding' => 'async'
   143â†’										]
   144â†’									);
   145â†’									echo $item['label'] ? '<span>' . $item['label'] . '</span>' : '';
   146â†’									echo $item['link'] ? '</a>' : '';
   147â†’								}
   148â†’							?>
   149â†’            </div>
   150â†’					
   151â†’					<?= $i === $count - 1 || ++$i % 3 === 0 ? '</div>' : ''; ?>
   152â†’				<?php } ?>
   153â†’      </div>
   154â†’		<?php
   155â†’		$output = ob_get_contents();
   156â†’		ob_end_clean();
   157â†’		
   158â†’		return $output;
   159â†’	}
