   164â†’	function render_ak_print_sizechart() {
   165â†’		global $post, $product; // Make sure $post and $product are defined before this function is called.
   166â†’		
   167â†’		$size_chart = get_field( 'size_chart', $post ); // Assuming you're using the Advanced Custom Fields (ACF) function here.
   168â†’		
   169â†’		if ( $size_chart !== '' ) { // Use strict comparison (!==) for better accuracy.
   170â†’			return $size_chart;
   171â†’		} else {
   172â†’			$size_chart_cat = '';
   173â†’			$parent_terms_post = get_the_terms( $post->ID, 'product_cat' ); // Assuming this function is used within the context where $post->ID makes sense.
   174â†’			
   175â†’			if ( ! is_wp_error( $parent_terms_post ) ) { // Check if $parent_terms_post is not an error object.
   176â†’				foreach ( $parent_terms_post as $term_cat ) {
   177â†’					$child_terms = wp_get_post_terms( $post->ID, 'product_cat', [ 'parent' => $term_cat->term_id ] );
   178â†’					
   179â†’					foreach ( $child_terms as $child_term_cat ) {
   180â†’						$size_chart_category = get_field( 'size_chart', $child_term_cat );
   181â†’						
   182â†’						if ( ! empty( $size_chart_category ) ) { // Use empty() to check for emptiness.
   183â†’							$size_chart_cat = $size_chart_category;
   184â†’						}
   185â†’					}
   186â†’				}
   187â†’			}
   188â†’			
   189â†’			return $size_chart_cat;
   190â†’		}
   191â†’	}
