   719â†’	function wc_auto_generate_variations_skus( $product ) {
   720â†’		if( $product->is_type('variable') && isset($_POST['generate_variations_skus']) ) {
   721â†’			$parent_sku   = $product->get_sku();
   722â†’			$children_ids = $product->get_children();
   723â†’			$count        = 0;
   724â†’			
