   726â†’			foreach( $children_ids as $child_id ){
   727â†’				$count++;
   728â†’				
   729â†’				// Get an instance of the WC_Product_Variation object
   730â†’				$variation = wc_get_product( $child_id );
   731â†’				
   732â†’				// Set the prefix length based on variations count
   733â†’				//$prefix = sizeof($children_ids) < 100 ? sprintf('%02d', $count) : sprintf('%03d', $count);
   734â†’				
   735â†’				$prefix = str_replace( ',' , '_', $variation->get_attribute( 'pa_size' ) );
   736â†’				
   737â†’				// Generate and set the sku
   738â†’				$variation->set_sku( $parent_sku . '-' . $prefix );
   739â†’				
   740â†’				// Save variation
   741â†’				$variation->save();
   742â†’			}
   743â†’		}
   744â†’	}
