   759â†’		if ( $product->is_type( 'simple' ) ) {
   760â†’			$max_percentage = ( ( $product->get_regular_price() - $product->get_sale_price() ) / $product->get_regular_price() ) * 100;
   761â†’		} elseif ( $product->is_type( 'variable' ) ) {
   762â†’			$max_percentage = 0;
   763â†’			foreach ( $product->get_children() as $child_id ) {
   764â†’				$variation = wc_get_product( $child_id );
   765â†’				$price = $variation->get_regular_price();
   766â†’				$sale = $variation->get_sale_price();
   767â†’				if ( $price != 0 && ! empty( $sale ) ) $percentage = ( $price - $sale ) / $price * 100;
   768â†’				if ( $percentage > $max_percentage ) {
   769â†’					$max_percentage = $percentage;
   770â†’				}
   771â†’			}
   772â†’		}
   773â†’		
   774â†’		$product_id = $product->get_id();
   775â†’		if ( has_term( 379, 'product_tag', $product_id ) && ! has_term( 387, 'product_tag', $product_id ) ) {
