   779â†’			
   780â†’			$isDaylightSaving = date( 'I' ); // 1 if in summer time, 0 if in winter time
   781â†’			
   782â†’			if ( $isDaylightSaving ) {
   783â†’				$sale_text = __( 'Summer sale', 'kraft' );
   784â†’			} else {
   785â†’				$current_date = date( 'Ymd' );
   786â†’				if ( $current_date >= 20241129 && $current_date <= 20241220 ) {
   787â†’					$sale_text = __( 'Black Friday', 'kraft' );
   788â†’				}
   789â†’        elseif ( $current_date >= 20231201 && $current_date <= 20240106 ) {
   790â†’					$sale_text = __( 'XMAS sale', 'kraft' );
   791â†’				}
   792â†’				else {
