   795â†’			}
   796â†’		}
   797â†’		
   798â†’		if ( $max_percentage > 0 ) echo "<span class='onsale'>" . $sale_text . " -" . round($max_percentage) . "%</span>"; // If you would like to show -40% off then add text after % sign
   799â†’	}
