   803â†’	function display_percentage_on_sale_badge( $html, $post, $product ) {
   804â†’		if ( $product->is_type( 'variable' ) ) {
   805â†’			$percentages = array();
   806â†’			
   807â†’			// This will get all the variation prices and loop throughout them
   808â†’			$prices = $product->get_variation_prices();
   809â†’			
   810â†’			foreach( $prices['price'] as $key => $price ){
   811â†’				// Only on sale variations
   812â†’				if( $prices['regular_price'][$key] !== $price ){
   813â†’					// Calculate and set in the array the percentage for each variation on sale
   814â†’					$percentages[] = round( 100 - ( floatval( $prices['sale_price'][$key]) / floatval($prices['regular_price'][$key] ) * 100 ) );
   815â†’				}
   816â†’			}
   817â†’			// Displays maximum discount value
   818â†’			$percentage = max($percentages) . '%';
   819â†’			
   820â†’		} elseif( $product->is_type( 'grouped' ) ) {
   821â†’			$percentages = array();
   822â†’			
   823â†’			// This will get all the variation prices and loop throughout them
   824â†’			$children_ids = $product->get_children();
   825â†’			
   826â†’			foreach( $children_ids as $child_id ) {
   827â†’				$child_product = wc_get_product( $child_id );
   828â†’				
   829â†’				$regular_price = (float) $child_product->get_regular_price();
   830â†’				$sale_price    = (float) $child_product->get_sale_price();
   831â†’				
   832â†’				if ( $sale_price != 0 || ! empty($sale_price) ) {
   833â†’					// Calculate and set in the array the percentage for each child on sale
   834â†’					$percentages[] = round(100 - ($sale_price / $regular_price * 100));
   835â†’				}
   836â†’			}
   837â†’			// Displays maximum discount value
   838â†’			$percentage = max( $percentages ) . '%';
   839â†’			
   840â†’		} else {
   841â†’			$regular_price = (float) $product->get_regular_price();
   842â†’			$sale_price    = (float) $product->get_sale_price();
   843â†’			
   844â†’			if ( $sale_price != 0 || ! empty( $sale_price ) ) {
   845â†’				$percentage    = round( 100 - ( $sale_price / $regular_price * 100 ) ) . '%';
   846â†’			} else {
   847â†’				return $html;
   848â†’			}
   849â†’		}
