   104→	function render_ak_home_composite( $atts ) {
   105→		global $post;
   106→		
   107→		// Recupera gli elementi
   108→		$items = get_field( 'grid-items', $post );
   109→		
   110→		// Assicurati che $items sia un array
   111→		if ( ! is_array( $items ) ) {
   112→			$items = [];
   113→		}
   114→		
   115→		$output = '';
   116→		$i = 0;
   117→		$count = count( $items ); // Ora sicuro perché $items è sempre un array
   118→		ob_start();
   119→		?>
   120→      <div class="grid composite">
   121→				<?php foreach ( $items as $item ) { ?>
   122→					<?= $i % 3 === 0 ? '<div class="col">' : ''; ?>
   123→            
   124→            <div class="item" <?= 'style="aspect-ratio: ' . $item['image']['width'] . ' / ' .  $item['image']['height'] . '"'; ?>>
   125→							<?php
   126→								if ( $item['shortcode'] ) {
   127→									echo do_shortcode( $item['shortcode'] );
   128→								} else {
   129→									$target = $item['external_link'] ? 'target="_blank"' : '';
   130→									echo $item['link'] ? '<a href="' . $item['link'] . '" ' . $target . ' style="display: block; aspect-ratio: ' . $item['image']['width'] . ' / ' .  $item['image']['height'] . '">' : '';
   131→									if ( $i === 2 || $i === 3 || $i === 8 ) {
   132→										$size = [726, 906];
   133→									} else {
   134→										$size = [352, 440];
   135→									}
   136→									echo wp_get_attachment_image(
   137→										$item['image']['ID'],
   138→										$size,
   139→										false,
   140→										[
   141→											'loading' => 'lazy',
   142→											'decoding' => 'async'
   143→										]
   144→									);
   145→									echo $item['label'] ? '<span>' . $item['label'] . '</span>' : '';
   146→									echo $item['link'] ? '</a>' : '';
   147→								}
   148→							?>
   149→            </div>
   150→					
   151→					<?= $i === $count - 1 || ++$i % 3 === 0 ? '</div>' : ''; ?>
   152→				<?php } ?>
   153→      </div>
   154→		<?php
   155→		$output = ob_get_contents();
   156→		ob_end_clean();
   157→		
   158→		return $output;
   159→	}
   160→	add_shortcode( 'ak_home_composite', 'render_ak_home_composite' );
