   164→	function render_ak_print_sizechart() {
   165→		global $post, $product; // Make sure $post and $product are defined before this function is called.
   166→		
   167→		$size_chart = get_field( 'size_chart', $post ); // Assuming you're using the Advanced Custom Fields (ACF) function here.
   168→		
   169→		if ( $size_chart !== '' ) { // Use strict comparison (!==) for better accuracy.
   170→			return $size_chart;
   171→		} else {
   172→			$size_chart_cat = '';
   173→			$parent_terms_post = get_the_terms( $post->ID, 'product_cat' ); // Assuming this function is used within the context where $post->ID makes sense.
   174→			
   175→			if ( ! is_wp_error( $parent_terms_post ) ) { // Check if $parent_terms_post is not an error object.
   176→				foreach ( $parent_terms_post as $term_cat ) {
   177→					$child_terms = wp_get_post_terms( $post->ID, 'product_cat', [ 'parent' => $term_cat->term_id ] );
   178→					
   179→					foreach ( $child_terms as $child_term_cat ) {
   180→						$size_chart_category = get_field( 'size_chart', $child_term_cat );
   181→						
   182→						if ( ! empty( $size_chart_category ) ) { // Use empty() to check for emptiness.
   183→							$size_chart_cat = $size_chart_category;
   184→						}
   185→					}
   186→				}
   187→			}
   188→			
   189→			return $size_chart_cat;
   190→		}
   191→	}
