   336→	function render_ak_category_breadcrumb() {
   337→		$queried_object = get_queried_object();
   338→		
   339→		$html = '';
   340→		
   341→		// Verifica che l'oggetto esista e che abbia la proprietà taxonomy
   342→		if ( is_object( $queried_object ) && property_exists( $queried_object, 'taxonomy' ) && $queried_object->taxonomy === 'product_cat' ) {
   343→			$term_id = $queried_object->term_id;
   344→			
   345→			if ( $queried_object->parent !== 0 ) {
   346→				$ancestors = get_ancestors( $queried_object->term_id, 'product_cat' );
   347→				$term_id = end( $ancestors );
   348→			}
   349→			
   350→			$term = get_term_by( 'ID', $term_id, 'product_cat' );
   351→			
   352→			if ( $term && ! is_wp_error( $term ) ) {
   353→				$html = '<nav class="woocommerce-breadcrumb"><a href="/">Home</a>&nbsp;/&nbsp;<a href="' . get_term_link( $term ) . '">' . $term->name . '</a></nav>';
   354→			}
   355→		}
   356→		
   357→		return $html;
