   465→	function add_product_to_cart() {
   466→		// Esegui solo se non è stato già aggiunto in questa sessione
   467→		if (!WC()->session->get('magazine_added') && !is_admin() && (is_cart() || is_checkout())) {
   468→			global $woocommerce;
   469→			$old_product_id = 317501; // AK magazine v4 Old variation product ID
   470→			$product_id = 321608; // AK magazine v5 Stylia variation product ID
   471→			$gift_card_id = 306636; // AK gift card product ID
   472→			$cart_total = 1; // cart must not be empty
   473→			
   474→			if ($woocommerce->cart->total >= $cart_total) {
   475→				// Check if any product already in cart
   476→				if (sizeof($woocommerce->cart->get_cart()) > 0) {
   477→					$is_valid_cart = true;
   478→					$magazine_already_in_cart = false;
   479→					
   480→					foreach ($woocommerce->cart->get_cart() as $cart_item_key => $values) {
   481→						$_product = $values['data'];
   482→						
   483→						if ($_product->get_id() == $old_product_id) {
   484→							$woocommerce->cart->remove_cart_item($cart_item_key);
   485→						}
   486→						
   487→						// Check if the new magazine is already in cart
   488→						if ($_product->get_id() == $product_id) {
