   756→	function sale_badge_percentage() {
   757→		global $product;
   758→		if ( ! $product->is_on_sale() ) return;
   759→		if ( $product->is_type( 'simple' ) ) {
   760→			$max_percentage = ( ( $product->get_regular_price() - $product->get_sale_price() ) / $product->get_regular_price() ) * 100;
   761→		} elseif ( $product->is_type( 'variable' ) ) {
   762→			$max_percentage = 0;
   763→			foreach ( $product->get_children() as $child_id ) {
   764→				$variation = wc_get_product( $child_id );
   765→				$price = $variation->get_regular_price();
   766→				$sale = $variation->get_sale_price();
   767→				if ( $price != 0 && ! empty( $sale ) ) $percentage = ( $price - $sale ) / $price * 100;
   768→				if ( $percentage > $max_percentage ) {
   769→					$max_percentage = $percentage;
   770→				}
   771→			}
   772→		}
   773→		
   774→		$product_id = $product->get_id();
   775→		if ( has_term( 379, 'product_tag', $product_id ) && ! has_term( 387, 'product_tag', $product_id ) ) {
   776→			$sale_text = __( 'Sale', 'kraft' );
   777→		} else {
   778→			date_default_timezone_set( 'Europe/Paris' ); // Set the timezone to CET
   779→			
   780→			$isDaylightSaving = date( 'I' ); // 1 if in summer time, 0 if in winter time
   781→			
   782→			if ( $isDaylightSaving ) {
   783→				$sale_text = __( 'Summer sale', 'kraft' );
   784→			} else {
   785→				$current_date = date( 'Ymd' );
   786→				if ( $current_date >= 20241129 && $current_date <= 20241220 ) {
   787→					$sale_text = __( 'Black Friday', 'kraft' );
   788→				}
   789→        elseif ( $current_date >= 20231201 && $current_date <= 20240106 ) {
   790→					$sale_text = __( 'XMAS sale', 'kraft' );
   791→				}
   792→				else {
   793→					$sale_text = __( 'Winter sale', 'kraft' );
   794→				}
   795→			}
   796→		}
   797→		
   798→		if ( $max_percentage > 0 ) echo "<span class='onsale'>" . $sale_text . " -" . round($max_percentage) . "%</span>"; // If you would like to show -40% off then add text after % sign
   799→	}
