   803→	function display_percentage_on_sale_badge( $html, $post, $product ) {
   804→		if ( $product->is_type( 'variable' ) ) {
   805→			$percentages = array();
   806→			
   807→			// This will get all the variation prices and loop throughout them
   808→			$prices = $product->get_variation_prices();
   809→			
   810→			foreach( $prices['price'] as $key => $price ){
   811→				// Only on sale variations
   812→				if( $prices['regular_price'][$key] !== $price ){
   813→					// Calculate and set in the array the percentage for each variation on sale
   814→					$percentages[] = round( 100 - ( floatval( $prices['sale_price'][$key]) / floatval($prices['regular_price'][$key] ) * 100 ) );
   815→				}
   816→			}
   817→			// Displays maximum discount value
   818→			$percentage = max($percentages) . '%';
   819→			
   820→		} elseif( $product->is_type( 'grouped' ) ) {
   821→			$percentages = array();
   822→			
   823→			// This will get all the variation prices and loop throughout them
   824→			$children_ids = $product->get_children();
   825→			
   826→			foreach( $children_ids as $child_id ) {
   827→				$child_product = wc_get_product( $child_id );
   828→				
   829→				$regular_price = (float) $child_product->get_regular_price();
   830→				$sale_price    = (float) $child_product->get_sale_price();
   831→				
   832→				if ( $sale_price != 0 || ! empty($sale_price) ) {
   833→					// Calculate and set in the array the percentage for each child on sale
   834→					$percentages[] = round(100 - ($sale_price / $regular_price * 100));
   835→				}
   836→			}
   837→			// Displays maximum discount value
   838→			$percentage = max( $percentages ) . '%';
   839→			
   840→		} else {
   841→			$regular_price = (float) $product->get_regular_price();
   842→			$sale_price    = (float) $product->get_sale_price();
   843→			
   844→			if ( $sale_price != 0 || ! empty( $sale_price ) ) {
   845→				$percentage    = round( 100 - ( $sale_price / $regular_price * 100 ) ) . '%';
   846→			} else {
   847→				return $html;
   848→			}
