remove_action( 'woocommerce_before_shop_loop', 'woocommerce_catalog_ordering', 30 );


function ak_change_breadcrumb( $breadcrumb ) {
	if ( is_singular() ) {
		array_pop( $breadcrumb );
	}
	return $breadcrumb;
}
add_filter( 'woocommerce_get_breadcrumb', 'ak_change_breadcrumb' );


add_filter( 'woocommerce_get_image_size_gallery_thumbnail', function( $size ) {
	return [
		'width'  => 100,
		'height' => 148,
		'crop'   => 0,
	];
} );


function fwp_wrapper_open() {
	if ( ! is_singular() ) : echo '<div class="facetwp-template">'; endif;
}
function fwp_wrapper_close() {
	if ( ! is_singular() ) : echo '</div><!-- end facetwp-template -->'; endif;
}
//add_action( 'woocommerce_before_shop_loop', 'fwp_wrapper_open', 5 );
//add_action( 'woocommerce_after_shop_loop', 'fwp_wrapper_close', 15 );
//add_action( 'woocommerce_no_products_found', 'fwp_wrapper_open', 5 );
//add_action( 'woocommerce_no_products_found', 'fwp_wrapper_close', 15 );


function render_ak_product_categories_by_parent( $atts ) {
	$a = shortcode_atts( [ 'id' => '' ], $atts );

	return '<ul>' . wp_list_categories(
			[
				'echo' => 0,
				'title_li' => '',
				'taxonomy' => 'product_cat',
				'child_of' => $a['id'],
				'depth' => 1,
				'orderby' => 'term_order'
			]
		) . '</ul>';
}
add_shortcode( 'ak_product_categories_by_parent', 'render_ak_product_categories_by_parent' );


add_filter( 'woocommerce_catalog_orderby', function ( $options ) {
	$options['menu_order'] = __( 'Sort', 'ak-hello-theme' );
	return $options;
}, 20 );


add_filter( 'gettext', function( $translated_text, $text, $domain ) {
	if ( 'fwp-front' == $domain && 'No results found' == $text ) {
		$translated_text = '';
	}

