		}

		$output = '';
		$i = 0;
		$count = count( $items ); // Ora sicuro perché $items è sempre un array
		ob_start();
		?>
      <div class="grid composite">
				<?php foreach ( $items as $item ) { ?>
					<?= $i % 3 === 0 ? '<div class="col">' : ''; ?>

            <div class="item" <?= 'style="aspect-ratio: ' . $item['image']['width'] . ' / ' .  $item['image']['height'] . '"'; ?>>
							<?php
								if ( $item['shortcode'] ) {
