   119‚Üí/**
   120‚Üí * Get a custom archive products title based on url parameters
   121‚Üí *
   122‚Üí * @return string|void
   123‚Üí */
   124‚Üífunction get_custom_shop_archive_title() {
   125‚Üí    if (!is_shop() && !defined('DOING_AJAX')) {
   126‚Üí        return '';
   127‚Üí    }
   128‚Üí
   129‚Üí    $price_range = isset($_GET['_price_range']) ? explode(',', $_GET['_price_range']) : [];
   130‚Üí    $min_price = isset($price_range[0]) ? floatval($price_range[0]) : null;
   131‚Üí    $max_price = isset($price_range[1]) ? floatval($price_range[1]) : null;
   132‚Üí
   133‚Üí    $brand_slug = isset($_GET['_product_categories']) ? sanitize_text_field($_GET['_product_categories']) : null;
   134‚Üí    $product_group = isset($_GET['_product_group']) ? sanitize_text_field($_GET['_product_group']) : null;
   135‚Üí    $manufacturer_warranty = isset($_GET['_manufacturer_warranty']) ? sanitize_text_field($_GET['_manufacturer_warranty']) : null;
   136‚Üí    $has_original_box = isset($_GET['_box']) ? sanitize_text_field($_GET['_box']) : null;
   137‚Üí
   138‚Üí    $brand_name = $brand_slug ? ucwords(str_replace('-', ' ', strtolower($brand_slug))) : null;
   139‚Üí    $group_label = $product_group ? ucwords(str_replace('-', ' ', strtolower($product_group))) : null;
   140‚Üí
   141‚Üí    $full_set_label = ($manufacturer_warranty === 'originale' && $has_original_box)
   142‚Üí        ? __('Full Set', 'sage')
   143‚Üí        : null;
   144‚Üí
   145‚Üí    $price_label = null;
   146‚Üí    if ($min_price || $max_price) {
   147‚Üí        if (($min_price && $max_price) && $min_price != 0) {
   148‚Üí            $price_label = sprintf(__('between %s and %s', 'sage'), wc_price($min_price), wc_price($max_price));
   149‚Üí        } elseif ($min_price > 0) {
   150‚Üí            $price_label = sprintf(__('over %s', 'sage'), wc_price($min_price));
   151‚Üí        } elseif ($max_price && $min_price == 0) {
   152‚Üí            $price_label = sprintf(__('under %s', 'sage'), wc_price($max_price));
   153‚Üí        }
   154‚Üí    }
   155‚Üí
   156‚Üí    return trim(implode(' ', array_filter([
   157‚Üí        __('Watches', 'sage'),
   158‚Üí        $brand_name,
   159‚Üí        $group_label,
   160‚Üí        $full_set_label,
   161‚Üí        $price_label,
   162‚Üí    ])));
   163‚Üí}
