   290‚Üí    /**
   291‚Üí     * Gestisci richiesta AJAX per generare descrizione
   292‚Üí     */
   293‚Üí    public function handle_generate_description() {
   294‚Üí        // Verifica nonce
   295‚Üí        if (!wp_verify_nonce($_POST['nonce'], 'aiwd_generate_nonce')) {
   296‚Üí            wp_die('Nonce verification failed');
   297‚Üí        }
   298‚Üí        
   299‚Üí        $product_id = intval($_POST['product_id']);
   300‚Üí        $product = wc_get_product($product_id);
   301‚Üí        
   302‚Üí        if (!$product) {
   303‚Üí            wp_send_json_error(array('message' => 'Prodotto non trovato'));
   304‚Üí        }
   305‚Üí        
   306‚Üí        // Estrai informazioni dal prodotto
   307‚Üí        $product_data = $this->extract_product_data($product);
   308‚Üí        
   309‚Üí        // Genera descrizione con OpenAI
   310‚Üí        $description = $this->generate_description_with_openai($product_data);
   311‚Üí        
   312‚Üí        if (is_wp_error($description)) {
   313‚Üí            wp_send_json_error(array('message' => $description->get_error_message()));
   314‚Üí        }
   315‚Üí        
   316‚Üí        // Verifica unicit√†
   317‚Üí        if ($this->is_duplicate_description($description, $product_id)) {
   318‚Üí            // Rigenera con variazione
   319‚Üí            $description = $this->generate_description_with_openai($product_data, true);
   320‚Üí        }
   321‚Üí        
   322‚Üí        // Salva nel database
   323‚Üí        $this->save_generated_description($product_id, $description);
   324‚Üí        
   325‚Üí        wp_send_json_success(array('description' => $description));
   326‚Üí    }
   327‚Üí    
   328‚Üí    /**
   329‚Üí     * Estrai dati del prodotto
   330‚Üí     */
   331‚Üí    private function extract_product_data($product) {
   332‚Üí        $data = array(
   333‚Üí            'name' => $product->get_name(),
   334‚Üí            'sku' => $product->get_sku(),
   335‚Üí            'price' => $product->get_price(),
   336‚Üí            'short_description' => $product->get_short_description(),
   337‚Üí            'description' => $product->get_description(),
   338‚Üí            'attributes' => array()
   339‚Üí        );
   340‚Üí        
   341‚Üí        // Estrai attributi
   342‚Üí        foreach ($product->get_attributes() as $attribute) {
   343‚Üí            $data['attributes'][$attribute->get_name()] = $attribute->get_options();
   344‚Üí        }
   345‚Üí        
   346‚Üí        // Estrai metadati personalizzati
   347‚Üí        $meta_keys = array('_watch_brand', '_watch_model', '_watch_condition', '_watch_box', '_watch_warranty', '_watch_year');
   348‚Üí        foreach ($meta_keys as $key) {
   349‚Üí            $value = get_post_meta($product->get_id(), $key, true);
   350‚Üí            if ($value) {
   351‚Üí                $data['meta'][str_replace('_watch_', '', $key)] = $value;
   352‚Üí            }
   353‚Üí        }
   354‚Üí        
   355‚Üí        // Estrai contenuto HTML della pagina
   356‚Üí        $data['page_content'] = $this->extract_page_content($product->get_id());
   357‚Üí        
   358‚Üí        return $data;
   359‚Üí    }
   360‚Üí    
   361‚Üí    /**
   362‚Üí     * Estrai contenuto della pagina
   363‚Üí     */
   364‚Üí    private function extract_page_content($product_id) {
   365‚Üí        $post = get_post($product_id);
   366‚Üí        $content = $post->post_content;
   367‚Üí        
   368‚Üí        // Rimuovi shortcode e HTML
   369‚Üí        $content = strip_shortcodes($content);
   370‚Üí        $content = wp_strip_all_tags($content);
   371‚Üí        
   372‚Üí        // Limita lunghezza
   373‚Üí        $content = substr($content, 0, 2000);
   374‚Üí        
   375‚Üí        return $content;
   376‚Üí    }
   377‚Üí    
   378‚Üí    /**
   379‚Üí     * Genera descrizione con OpenAI
   380‚Üí     */
   381‚Üí    private function generate_description_with_openai($product_data, $variation = false) {
   382‚Üí        $api_key = get_option('aiwd_openai_key');
   383‚Üí        if (!$api_key) {
   384‚Üí            return new WP_Error('no_api_key', 'Chiave API OpenAI non configurata');
   385‚Üí        }
   386‚Üí        
   387‚Üí        $prompt = $this->build_prompt($product_data, $variation);
   388‚Üí        
   389‚Üí        $response = wp_remote_post('https://api.openai.com/v1/chat/completions', array(
   390‚Üí            'headers' => array(
   391‚Üí                'Authorization' => 'Bearer ' . $api_key,
   392‚Üí                'Content-Type' => 'application/json'
   393‚Üí            ),
   394‚Üí            'body' => json_encode(array(
   395‚Üí                'model' => get_option('aiwd_model', 'gpt-3.5-turbo'),
   396‚Üí                'messages' => array(
   397‚Üí                    array(
   398‚Üí                        'role' => 'system',
   399‚Üí                        'content' => 'Sei un esperto di orologi di lusso che crea descrizioni concise e professionali per un sito di gioielleria.'
   400‚Üí                    ),
   401‚Üí                    array(
   402‚Üí                        'role' => 'user',
   403‚Üí                        'content' => $prompt
   404‚Üí                    )
   405‚Üí                ),
   406‚Üí                'max_tokens' => 150,
   407‚Üí                'temperature' => $variation ? 0.8 : 0.7
   408‚Üí            )),
   409‚Üí            'timeout' => 30
   410‚Üí        ));
   411‚Üí        
   412‚Üí        if (is_wp_error($response)) {
   413‚Üí            return $response;
   414‚Üí        }
   415‚Üí        
   416‚Üí        $body = wp_remote_retrieve_body($response);
   417‚Üí        $data = json_decode($body, true);
   418‚Üí        
   419‚Üí        if (isset($data['choices'][0]['message']['content'])) {
   420‚Üí            return trim($data['choices'][0]['message']['content']);
   421‚Üí        }
   422‚Üí        
   423‚Üí        return new WP_Error('api_error', 'Errore nella risposta API');
   424‚Üí    }
   425‚Üí    
   426‚Üí    /**
   427‚Üí     * Costruisci prompt per OpenAI
   428‚Üí     */
   429‚Üí    private function build_prompt($product_data, $variation = false) {
   430‚Üí        $prompt = "Crea una descrizione di massimo 5 righe per questo orologio di lusso:\n\n";
   431‚Üí        
   432‚Üí        $prompt .= "Nome: " . $product_data['name'] . "\n";
   433‚Üí        
   434‚Üí        if (!empty($product_data['meta'])) {
   435‚Üí            foreach ($product_data['meta'] as $key => $value) {
   436‚Üí                $prompt .= ucfirst($key) . ": " . $value . "\n";
   437‚Üí            }
   438‚Üí        }
   439‚Üí        
   440‚Üí        if (!empty($product_data['attributes'])) {
   441‚Üí            foreach ($product_data['attributes'] as $name => $values) {
   442‚Üí                $prompt .= $name . ": " . implode(', ', $values) . "\n";
   443‚Üí            }
   444‚Üí        }
   445‚Üí        
   446‚Üí        if (!empty($product_data['page_content'])) {
   447‚Üí            $prompt .= "\nContenuto pagina: " . substr($product_data['page_content'], 0, 500) . "\n";
   448‚Üí        }
   449‚Üí        
   450‚Üí        $prompt .= "\nLa descrizione deve includere:\n";
   451‚Üí        $prompt .= "- Breve introduzione dell'orologio\n";
   452‚Üí        $prompt .= "- Caratteristiche tecniche principali\n";
   453‚Üí        $prompt .= "- Condizioni dell'orologio\n";
   454‚Üí        $prompt .= "- Informazioni su scatola e garanzia originale\n";
   455‚Üí        
   456‚Üí        if ($variation) {
   457‚Üí            $prompt .= "\nIMPORTANTE: Crea una descrizione unica e diversa dalle precedenti, usando sinonimi e struttura diversa.";
   458‚Üí        }
   459‚Üí        
   460‚Üí        return $prompt;
   461‚Üí    }
   462‚Üí    
   463‚Üí    /**
   464‚Üí     * Verifica se la descrizione √® duplicata
   465‚Üí     */
   466‚Üí    private function is_duplicate_description($description, $current_product_id) {
   467‚Üí        global $wpdb;
   468‚Üí        
   469‚Üí        // Calcola hash della descrizione
   470‚Üí        $hash = md5(strtolower(str_replace(' ', '', $description)));
   471‚Üí        
   472‚Üí        // Cerca nel database delle descrizioni generate
   473‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   474‚Üí        $exists = $wpdb->get_var($wpdb->prepare(
   475‚Üí            "SELECT COUNT(*) FROM $table_name WHERE hash = %s AND product_id != %d",
   476‚Üí            $hash,
   477‚Üí            $current_product_id
   478‚Üí        ));
   479‚Üí        
   480‚Üí        if ($exists > 0) {
   481‚Üí            return true;
   482‚Üí        }
   483‚Üí        
   484‚Üí        // Cerca nelle descrizioni esistenti dei prodotti
   485‚Üí        $similar_products = $wpdb->get_var($wpdb->prepare(
   486‚Üí            "SELECT COUNT(*) FROM {$wpdb->posts} p
   487‚Üí            JOIN {$wpdb->postmeta} pm ON p.ID = pm.post_id
   488‚Üí            WHERE p.post_type = 'product'
   489‚Üí            AND p.ID != %d
   490‚Üí            AND (
   491‚Üí                p.post_excerpt LIKE %s
   492‚Üí                OR p.post_content LIKE %s
   493‚Üí                OR pm.meta_value LIKE %s
   494‚Üí            )",
   495‚Üí            $current_product_id,
   496‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   497‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   498‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%'
   499‚Üí        ));
   500‚Üí        
   501‚Üí        return $similar_products > 0;
   502‚Üí    }
