   504‚Üí    /**
   505‚Üí     * Salva descrizione generata
   506‚Üí     */
   507‚Üí    private function save_generated_description($product_id, $description) {
   508‚Üí        global $wpdb;
   509‚Üí        
   510‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   511‚Üí        $wpdb->insert(
   512‚Üí            $table_name,
   513‚Üí            array(
   514‚Üí                'product_id' => $product_id,
   515‚Üí                'description' => $description,
   516‚Üí                'hash' => md5(strtolower(str_replace(' ', '', $description)))
   517‚Üí            )
   518‚Üí        );
   519‚Üí    }
