   662‚Üí    /**
   663‚Üí     * Esporta descrizioni in CSV
   664‚Üí     */
   665‚Üí    private function export_descriptions() {
   666‚Üí        global $wpdb;
   667‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   668‚Üí        
   669‚Üí        $results = $wpdb->get_results(
   670‚Üí            "SELECT d.*, p.post_title 
   671‚Üí             FROM $table_name d
   672‚Üí             LEFT JOIN {$wpdb->posts} p ON d.product_id = p.ID
   673‚Üí             ORDER BY d.created_at DESC"
   674‚Üí        );
   675‚Üí        
   676‚Üí        header('Content-Type: text/csv; charset=utf-8');
   677‚Üí        header('Content-Disposition: attachment; filename=aiwd-descriptions-' . date('Y-m-d') . '.csv');
   678‚Üí        
   679‚Üí        $output = fopen('php://output', 'w');
   680‚Üí        fprintf($output, chr(0xEF).chr(0xBB).chr(0xBF)); // BOM per UTF-8
   681‚Üí        
   682‚Üí        fputcsv($output, array('ID', 'Product ID', 'Product Name', 'Description', 'Hash', 'Created At'));
   683‚Üí        
   684‚Üí        foreach ($results as $row) {
   685‚Üí            fputcsv($output, array(
   686‚Üí                $row->id,
   687‚Üí                $row->product_id,
   688‚Üí                $row->post_title,
   689‚Üí                $row->description,
   690‚Üí                $row->hash,
   691‚Üí                $row->created_at
   692‚Üí            ));
   693‚Üí        }
   694‚Üí        
   695‚Üí        fclose($output);
   696‚Üí        exit;
   697‚Üí    }
   698‚Üí}
   699‚Üí
   700‚Üí// Inizializza il plugin
   701‚Üíadd_action('plugins_loaded', array('AI_Watch_Description_Generator', 'get_instance'));
   702‚Üí
   703‚Üí// Registra impostazioni
   704‚Üíadd_action('admin_init', function() {
   705‚Üí    register_setting('aiwd_settings', 'aiwd_openai_key');
   706‚Üí    register_setting('aiwd_settings', 'aiwd_model');
   707‚Üí});
