private function build_prompt($product_data, $variation = false) {
    $prompt = "Crea una descrizione di massimo 5 righe per questo orologio di lusso:\n\n";

    $prompt .= "Nome: " . $product_data['name'] . "\n";

    if (!empty($product_data['meta'])) {
        foreach ($product_data['meta'] as $key => $value) {
            $prompt .= ucfirst($key) . ": " . $value . "\n";
        }
    }

    if (!empty($product_data['attributes'])) {
        foreach ($product_data['attributes'] as $name => $values) {
            $prompt .= $name . ": " . implode(', ', $values) . "\n";
        }
    }

    if (!empty($product_data['page_content'])) {
        $prompt .= "\nContenuto pagina: " . substr($product_data['page_content'], 0, 500) . "\n";
    }

    $prompt .= "\nLa descrizione deve includere:\n";
    $prompt .= "- Breve introduzione dell'orologio\n";
    $prompt .= "- Caratteristiche tecniche principali\n";
    $prompt .= "- Condizioni dell'orologio\n";
    $prompt .= "- Informazioni su scatola e garanzia originale\n";

    if ($variation) {
        $prompt .= "\nIMPORTANTE: Crea una descrizione unica e diversa dalle precedenti, usando sinonimi e struttura diversa.";
    }

