private function build_prompt($product_data, $variation = false) {
    $prompt = "Crea una descrizione professionale di massimo 5 righe per questo orologio di lusso da gioielleria:\n\n";

    // Estrai brand dalla categoria prodotto
    $brand = '';
    if (!empty($product_data['categories'])) {
        $brand = $product_data['categories'][0];
    }

    $prompt .= "OROLOGIO: " . $product_data['name'] . "\n";
    if ($brand) $prompt .= "MARCA: " . $brand . "\n";

    // Campi specifici ACF del sito
    $meta = $product_data['meta'];
    if (!empty($meta['watch_reference'])) $prompt .= "REFERENZA: " . $meta['watch_reference'] . "\n";
    if (!empty($meta['watch_production_year'])) $prompt .= "ANNO: " . $meta['watch_production_year'] . "\n";
    if (!empty($meta['watch_case'])) $prompt .= "CASSA: " . $meta['watch_case'] . "\n";
    if (!empty($meta['watch_dimension'])) $prompt .= "DIMENSIONI: " . $meta['watch_dimension'] . "mm\n";
    if (!empty($meta['watch_movement'])) $prompt .= "MOVIMENTO: " . $meta['watch_movement'] . "\n";
    if (!empty($meta['watch_dial'])) $prompt .= "QUADRANTE: " . $meta['watch_dial'] . "\n";
    if (!empty($meta['watch_bracelet'])) $prompt .= "BRACCIALE: " . $meta['watch_bracelet'] . "\n";
    if (!empty($meta['watch_status'])) $prompt .= "CONDIZIONI: " . $meta['watch_status'] . "\n";

    // Accessori e garanzia
    $box = !empty($meta['watch_box']) && $meta['watch_box'] ? 'S√å' : 'NO';
    $warranty = !empty($meta['watch_warranty']) ? $meta['watch_warranty'] : 'no';
    $prompt .= "SCATOLA ORIGINALE: " . $box . "\n";
    $prompt .= "GARANZIA: " . ucfirst($warranty) . "\n";

    if (!empty($meta['watch_notes'])) {
        $prompt .= "NOTE: " . $meta['watch_notes'] . "\n";
    }

    $prompt .= "\nCREA UNA DESCRIZIONE CHE:\n";
    $prompt .= "- Introduca elegantemente l'orologio con marca e modello\n";
    $prompt .= "- Evidenzi le caratteristiche tecniche pi√π importanti (cassa, movimento, dimensioni)\n";
    $prompt .= "- Specifichi chiaramente le condizioni (nuovo/usato)\n";
    $prompt .= "- Indichi disponibilit√† di scatola e tipo di garanzia\n";
    $prompt .= "- Usi un linguaggio professionale da gioielleria di lusso\n";

    if ($variation) {
        $prompt .= "\nIMPORTANTE: Crea una descrizione completamente diversa dalle precedenti, con struttura e terminologia alternative.";
    }

