private function extract_product_data($product) {
    $data = array(
        'name' => $product->get_name(),
        'sku' => $product->get_sku(),
        'price' => $product->get_price(),
        'short_description' => $product->get_short_description(),
        'description' => $product->get_description(),
        'attributes' => array(),
        'categories' => array(),
        'product_groups' => array()
    );

    // Estrai categorie prodotto (per il brand)
    $categories = wp_get_post_terms($product->get_id(), 'product_cat');
    foreach ($categories as $category) {
        $data['categories'][] = $category->name;
    }

    // Estrai product_group (tassonomia specifica del sito)
    $product_groups = wp_get_post_terms($product->get_id(), 'product_group');
    foreach ($product_groups as $group) {
        $data['product_groups'][] = $group->slug;
    }

