   444‚Üí    /**
   445‚Üí     * Costruisci prompt per OpenAI
   446‚Üí     */
   447‚Üí    private function build_prompt($product_data, $variation = false) {
   448‚Üí        $prompt = "Crea una descrizione professionale di massimo 5 righe per questo orologio di lusso da gioielleria:\n\n";
   449‚Üí        
   450‚Üí        // Estrai brand dalla categoria prodotto
   451‚Üí        $brand = '';
   452‚Üí        if (!empty($product_data['categories'])) {
   453‚Üí            $brand = $product_data['categories'][0];
   454‚Üí        }
   455‚Üí        
   456‚Üí        $prompt .= "OROLOGIO: " . $product_data['name'] . "\n";
   457‚Üí        if ($brand) $prompt .= "MARCA: " . $brand . "\n";
   458‚Üí        
   459‚Üí        // Campi specifici ACF del sito
   460‚Üí        $meta = $product_data['meta'];
   461‚Üí        if (!empty($meta['watch_reference'])) $prompt .= "REFERENZA: " . $meta['watch_reference'] . "\n";
   462‚Üí        if (!empty($meta['watch_production_year'])) $prompt .= "ANNO: " . $meta['watch_production_year'] . "\n";
   463‚Üí        if (!empty($meta['watch_case'])) $prompt .= "CASSA: " . $meta['watch_case'] . "\n";
   464‚Üí        if (!empty($meta['watch_dimension'])) $prompt .= "DIMENSIONI: " . $meta['watch_dimension'] . "mm\n";
   465‚Üí        if (!empty($meta['watch_movement'])) $prompt .= "MOVIMENTO: " . $meta['watch_movement'] . "\n";
   466‚Üí        if (!empty($meta['watch_dial'])) $prompt .= "QUADRANTE: " . $meta['watch_dial'] . "\n";
   467‚Üí        if (!empty($meta['watch_bracelet'])) $prompt .= "BRACCIALE: " . $meta['watch_bracelet'] . "\n";
   468‚Üí        if (!empty($meta['watch_status'])) $prompt .= "CONDIZIONI: " . $meta['watch_status'] . "\n";
   469‚Üí        
   470‚Üí        // Accessori e garanzia
   471‚Üí        $box = !empty($meta['watch_box']) && $meta['watch_box'] ? 'S√å' : 'NO';
   472‚Üí        $warranty = !empty($meta['watch_warranty']) ? $meta['watch_warranty'] : 'no';
   473‚Üí        $prompt .= "SCATOLA ORIGINALE: " . $box . "\n";
   474‚Üí        $prompt .= "GARANZIA: " . ucfirst($warranty) . "\n";
   475‚Üí        
   476‚Üí        if (!empty($meta['watch_notes'])) {
   477‚Üí            $prompt .= "NOTE: " . $meta['watch_notes'] . "\n";
   478‚Üí        }
   479‚Üí        
   480‚Üí        $prompt .= "\nCREA UNA DESCRIZIONE CHE:\n";
   481‚Üí        $prompt .= "- Introduca elegantemente l'orologio con marca e modello\n";
   482‚Üí        $prompt .= "- Evidenzi le caratteristiche tecniche pi√π importanti (cassa, movimento, dimensioni)\n";
   483‚Üí        $prompt .= "- Specifichi chiaramente le condizioni (nuovo/usato)\n";
   484‚Üí        $prompt .= "- Indichi disponibilit√† di scatola e tipo di garanzia\n";
   485‚Üí        $prompt .= "- Usi un linguaggio professionale da gioielleria di lusso\n";
   486‚Üí        
   487‚Üí        if ($variation) {
   488‚Üí            $prompt .= "\nIMPORTANTE: Crea una descrizione completamente diversa dalle precedenti, con struttura e terminologia alternative.";
   489‚Üí        }
   490‚Üí        
   491‚Üí        return $prompt;
   492‚Üí    }
