   494‚Üí    /**
   495‚Üí     * Verifica se la descrizione √® duplicata
   496‚Üí     */
   497‚Üí    private function is_duplicate_description($description, $current_product_id) {
   498‚Üí        global $wpdb;
   499‚Üí        
   500‚Üí        // Calcola hash della descrizione
   501‚Üí        $hash = md5(strtolower(str_replace(' ', '', $description)));
   502‚Üí        
   503‚Üí        // Cerca nel database delle descrizioni generate
   504‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   505‚Üí        $exists = $wpdb->get_var($wpdb->prepare(
   506‚Üí            "SELECT COUNT(*) FROM $table_name WHERE hash = %s AND product_id != %d",
   507‚Üí            $hash,
   508‚Üí            $current_product_id
   509‚Üí        ));
   510‚Üí        
   511‚Üí        if ($exists > 0) {
   512‚Üí            return true;
   513‚Üí        }
   514‚Üí        
   515‚Üí        // Cerca nelle descrizioni esistenti dei prodotti
   516‚Üí        $similar_products = $wpdb->get_var($wpdb->prepare(
   517‚Üí            "SELECT COUNT(*) FROM {$wpdb->posts} p
   518‚Üí            JOIN {$wpdb->postmeta} pm ON p.ID = pm.post_id
   519‚Üí            WHERE p.post_type = 'product'
   520‚Üí            AND p.ID != %d
   521‚Üí            AND (
   522‚Üí                p.post_excerpt LIKE %s
   523‚Üí                OR p.post_content LIKE %s
   524‚Üí                OR pm.meta_value LIKE %s
   525‚Üí            )",
   526‚Üí            $current_product_id,
   527‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   528‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   529‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%'
   530‚Üí        ));
   531‚Üí        
   532‚Üí        return $similar_products > 0;
   533‚Üí    }
