   535‚Üí    /**
   536‚Üí     * Salva descrizione generata
   537‚Üí     */
   538‚Üí    private function save_generated_description($product_id, $description) {
   539‚Üí        global $wpdb;
   540‚Üí        
   541‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   542‚Üí        $wpdb->insert(
   543‚Üí            $table_name,
   544‚Üí            array(
