   696‚Üí    private function export_descriptions() {
   697‚Üí        global $wpdb;
   698‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   699‚Üí        
   700‚Üí        $results = $wpdb->get_results(
   701‚Üí            "SELECT d.*, p.post_title 
   702‚Üí             FROM $table_name d
   703‚Üí             LEFT JOIN {$wpdb->posts} p ON d.product_id = p.ID
   704‚Üí             ORDER BY d.created_at DESC"
   705‚Üí        );
   706‚Üí        
   707‚Üí        header('Content-Type: text/csv; charset=utf-8');
   708‚Üí        header('Content-Disposition: attachment; filename=aiwd-descriptions-' . date('Y-m-d') . '.csv');
   709‚Üí        
   710‚Üí        $output = fopen('php://output', 'w');
   711‚Üí        fprintf($output, chr(0xEF).chr(0xBB).chr(0xBF)); // BOM per UTF-8
   712‚Üí        
   713‚Üí        fputcsv($output, array('ID', 'Product ID', 'Product Name', 'Description', 'Hash', 'Created At'));
   714‚Üí        
   715‚Üí        foreach ($results as $row) {
   716‚Üí            fputcsv($output, array(
   717‚Üí                $row->id,
   718‚Üí                $row->product_id,
   719‚Üí                $row->post_title,
   720‚Üí                $row->description,
   721‚Üí                $row->hash,
   722‚Üí                $row->created_at
   723‚Üí            ));
   724‚Üí        }
   725‚Üí        
   726‚Üí        fclose($output);
   727‚Üí        exit;
   728‚Üí    }
