   379‚Üí    /**
   380‚Üí     * Estrai contenuto della pagina
   381‚Üí     */
   382‚Üí    private function extract_page_content($product_id) {
   383‚Üí        $post = get_post($product_id);
   384‚Üí        $content = $post->post_content;
   385‚Üí        
   386‚Üí        // Rimuovi shortcode e HTML
   387‚Üí        $content = strip_shortcodes($content);
   388‚Üí        $content = wp_strip_all_tags($content);
   389‚Üí        
   390‚Üí        // Limita lunghezza
   391‚Üí        $content = substr($content, 0, 2000);
   392‚Üí        
   393‚Üí        return $content;
   394‚Üí    }
