   396‚Üí    /**
   397‚Üí     * Genera descrizione con OpenAI
   398‚Üí     */
   399‚Üí    private function generate_description_with_openai($product_data, $variation = false) {
   400‚Üí        $api_key = get_option('aiwd_openai_key');
   401‚Üí        if (!$api_key) {
   402‚Üí            return new WP_Error('no_api_key', 'Chiave API OpenAI non configurata');
   403‚Üí        }
   404‚Üí        
   405‚Üí        $prompt = $this->build_prompt($product_data, $variation);
   406‚Üí        
   407‚Üí        $response = wp_remote_post('https://api.openai.com/v1/chat/completions', array(
   408‚Üí            'headers' => array(
   409‚Üí                'Authorization' => 'Bearer ' . $api_key,
   410‚Üí                'Content-Type' => 'application/json'
   411‚Üí            ),
   412‚Üí            'body' => json_encode(array(
   413‚Üí                'model' => get_option('aiwd_model', 'gpt-3.5-turbo'),
   414‚Üí                'messages' => array(
   415‚Üí                    array(
   416‚Üí                        'role' => 'system',
   417‚Üí                        'content' => 'Sei un esperto di orologi di lusso che crea descrizioni concise e professionali per un sito di gioielleria.'
   418‚Üí                    ),
   419‚Üí                    array(
   420‚Üí                        'role' => 'user',
   421‚Üí                        'content' => $prompt
   422‚Üí                    )
   423‚Üí                ),
   424‚Üí                'max_tokens' => 150,
   425‚Üí                'temperature' => $variation ? 0.8 : 0.7
   426‚Üí            )),
   427‚Üí            'timeout' => 30
   428‚Üí        ));
   429‚Üí        
   430‚Üí        if (is_wp_error($response)) {
   431‚Üí            return $response;
   432‚Üí        }
   433‚Üí        
   434‚Üí        $body = wp_remote_retrieve_body($response);
   435‚Üí        $data = json_decode($body, true);
   436‚Üí        
   437‚Üí        if (isset($data['choices'][0]['message']['content'])) {
   438‚Üí            return trim($data['choices'][0]['message']['content']);
   439‚Üí        }
   440‚Üí        
   441‚Üí        return new WP_Error('api_error', 'Errore nella risposta API');
   442‚Üí    }
