   444‚Üí    /**
   445‚Üí     * Costruisci prompt per OpenAI
   446‚Üí     */
   447‚Üí    private function build_prompt($product_data, $variation = false) {
   448‚Üí        $prompt = "Crea una descrizione professionale di massimo 5 righe per questo orologio di lusso da gioielleria:\n\n";
   449‚Üí        
   450‚Üí        // Estrai brand dalla categoria prodotto
   451‚Üí        $brand = '';
   452‚Üí        if (!empty($product_data['categories'])) {
   453‚Üí            $brand = $product_data['categories'][0];
   454‚Üí        }
   455‚Üí        
   456‚Üí        $prompt .= "OROLOGIO: " . $product_data['name'] . "\n";
   457‚Üí        if ($brand) $prompt .= "MARCA: " . $brand . "\n";
   458‚Üí        
   459‚Üí        // Aggiungi categoria prodotto per personalizzare il linguaggio
   460‚Üí        if (!empty($product_data['product_groups'])) {
   461‚Üí            $group = $product_data['product_groups'][0];
   462‚Üí            $prompt .= "CATEGORIA: " . ucfirst($group) . "\n";
   463‚Üí            
   464‚Üí            // Personalizza approccio per categoria
   465‚Üí            if ($group === 'vintage') {
   466‚Üí                $prompt .= "STILE: Orologio d'epoca di valore storico\n";
   467‚Üí            } elseif ($group === 'premium') {
   468‚Üí                $prompt .= "STILE: Pezzo di alta orologeria di lusso\n";
   469‚Üí            } elseif ($group === 'accessories') {
   470‚Üí                $prompt .= "STILE: Accessorio per orologeria\n";
   471‚Üí            }
   472‚Üí        }
   473‚Üí        
   474‚Üí        // Campi specifici ACF del sito
   475‚Üí        $meta = $product_data['meta'];
   476‚Üí        if (!empty($meta['watch_reference'])) $prompt .= "REFERENZA: " . $meta['watch_reference'] . "\n";
   477‚Üí        if (!empty($meta['watch_production_year'])) $prompt .= "ANNO: " . $meta['watch_production_year'] . "\n";
   478‚Üí        if (!empty($meta['watch_case'])) $prompt .= "CASSA: " . $meta['watch_case'] . "\n";
   479‚Üí        if (!empty($meta['watch_dimension'])) $prompt .= "DIMENSIONI: " . $meta['watch_dimension'] . "mm\n";
   480‚Üí        if (!empty($meta['watch_movement'])) $prompt .= "MOVIMENTO: " . $meta['watch_movement'] . "\n";
   481‚Üí        if (!empty($meta['watch_dial'])) $prompt .= "QUADRANTE: " . $meta['watch_dial'] . "\n";
   482‚Üí        if (!empty($meta['watch_bracelet'])) $prompt .= "BRACCIALE: " . $meta['watch_bracelet'] . "\n";
   483‚Üí        if (!empty($meta['watch_status'])) $prompt .= "CONDIZIONI: " . $meta['watch_status'] . "\n";
   484‚Üí        
   485‚Üí        // Accessori e garanzia
   486‚Üí        $box = !empty($meta['watch_box']) && $meta['watch_box'] ? 'S√å' : 'NO';
   487‚Üí        $warranty = !empty($meta['watch_warranty']) ? $meta['watch_warranty'] : 'no';
   488‚Üí        $prompt .= "SCATOLA ORIGINALE: " . $box . "\n";
   489‚Üí        $prompt .= "GARANZIA: " . ucfirst($warranty) . "\n";
   490‚Üí        
   491‚Üí        if (!empty($meta['watch_notes'])) {
   492‚Üí            $prompt .= "NOTE: " . $meta['watch_notes'] . "\n";
   493‚Üí        }
   494‚Üí        
   495‚Üí        $prompt .= "\nCREA UNA DESCRIZIONE CHE:\n";
   496‚Üí        $prompt .= "- Introduca elegantemente l'orologio con marca e modello\n";
   497‚Üí        $prompt .= "- Evidenzi le caratteristiche tecniche pi√π importanti (cassa, movimento, dimensioni)\n";
   498‚Üí        $prompt .= "- Specifichi chiaramente le condizioni (nuovo/usato)\n";
   499‚Üí        $prompt .= "- Indichi disponibilit√† di scatola e tipo di garanzia\n";
   500‚Üí        $prompt .= "- Usi un linguaggio professionale da gioielleria di lusso\n";
   501‚Üí        
   502‚Üí        if ($variation) {
   503‚Üí            $prompt .= "\nIMPORTANTE: Crea una descrizione completamente diversa dalle precedenti, con struttura e terminologia alternative.";
   504‚Üí        }
   505‚Üí        
   506‚Üí        return $prompt;
   507‚Üí    }
