   509‚Üí    /**
   510‚Üí     * Verifica se la descrizione √® duplicata
   511‚Üí     */
   512‚Üí    private function is_duplicate_description($description, $current_product_id) {
   513‚Üí        global $wpdb;
   514‚Üí        
   515‚Üí        // Calcola hash della descrizione
   516‚Üí        $hash = md5(strtolower(str_replace(' ', '', $description)));
   517‚Üí        
   518‚Üí        // Cerca nel database delle descrizioni generate
   519‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   520‚Üí        $exists = $wpdb->get_var($wpdb->prepare(
   521‚Üí            "SELECT COUNT(*) FROM $table_name WHERE hash = %s AND product_id != %d",
   522‚Üí            $hash,
   523‚Üí            $current_product_id
   524‚Üí        ));
   525‚Üí        
   526‚Üí        if ($exists > 0) {
   527‚Üí            return true;
   528‚Üí        }
   529‚Üí        
   530‚Üí        // Cerca nelle descrizioni esistenti dei prodotti
   531‚Üí        $similar_products = $wpdb->get_var($wpdb->prepare(
   532‚Üí            "SELECT COUNT(*) FROM {$wpdb->posts} p
   533‚Üí            JOIN {$wpdb->postmeta} pm ON p.ID = pm.post_id
   534‚Üí            WHERE p.post_type = 'product'
   535‚Üí            AND p.ID != %d
   536‚Üí            AND (
   537‚Üí                p.post_excerpt LIKE %s
   538‚Üí                OR p.post_content LIKE %s
   539‚Üí                OR pm.meta_value LIKE %s
   540‚Üí            )",
   541‚Üí            $current_product_id,
   542‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   543‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   544‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%'
   545‚Üí        ));
   546‚Üí        
   547‚Üí        return $similar_products > 0;
   548‚Üí    }
