   551‚Üí     * Salva descrizione generata
   552‚Üí     */
   553‚Üí    private function save_generated_description($product_id, $description) {
   554‚Üí        global $wpdb;
   555‚Üí        
   556‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
