   397‚Üí    /**
   398‚Üí     * Genera descrizione con OpenAI
   399‚Üí     */
   400‚Üí    private function generate_description_with_openai($product_data, $variation = false) {
   401‚Üí        $api_key = get_option('aiwd_openai_key');
   402‚Üí        if (!$api_key) {
   403‚Üí            return new WP_Error('no_api_key', 'Chiave API OpenAI non configurata');
   404‚Üí        }
   405‚Üí        
   406‚Üí        $prompt = $this->build_prompt($product_data, $variation);
   407‚Üí        
   408‚Üí        $response = wp_remote_post('https://api.openai.com/v1/chat/completions', array(
   409‚Üí            'headers' => array(
   410‚Üí                'Authorization' => 'Bearer ' . $api_key,
   411‚Üí                'Content-Type' => 'application/json'
   412‚Üí            ),
   413‚Üí            'body' => json_encode(array(
   414‚Üí                'model' => get_option('aiwd_model', 'gpt-3.5-turbo'),
   415‚Üí                'messages' => array(
   416‚Üí                    array(
   417‚Üí                        'role' => 'system',
   418‚Üí                        'content' => 'Sei un esperto di orologi di lusso che crea descrizioni concise e professionali per un sito di gioielleria.'
   419‚Üí                    ),
   420‚Üí                    array(
   421‚Üí                        'role' => 'user',
   422‚Üí                        'content' => $prompt
   423‚Üí                    )
   424‚Üí                ),
   425‚Üí                'max_tokens' => 150,
   426‚Üí                'temperature' => $variation ? 0.8 : 0.7
   427‚Üí            )),
   428‚Üí            'timeout' => 30
   429‚Üí        ));
   430‚Üí        
   431‚Üí        if (is_wp_error($response)) {
   432‚Üí            return $response;
   433‚Üí        }
   434‚Üí        
   435‚Üí        $body = wp_remote_retrieve_body($response);
   436‚Üí        $data = json_decode($body, true);
   437‚Üí        
   438‚Üí        if (isset($data['choices'][0]['message']['content'])) {
   439‚Üí            return trim($data['choices'][0]['message']['content']);
   440‚Üí        }
   441‚Üí        
   442‚Üí        return new WP_Error('api_error', 'Errore nella risposta API');
   443‚Üí    }
