   477‚Üí        if (!empty($meta['watch_reference'])) $prompt .= "REFERENZA: " . $meta['watch_reference'] . "\n";
   478‚Üí        if (!empty($meta['watch_production_year'])) $prompt .= "ANNO: " . $meta['watch_production_year'] . "\n";
