   487‚Üí        $box = !empty($meta['watch_box']) && $meta['watch_box'] ? 'S√å' : 'NO';
   488‚Üí        $warranty = !empty($meta['watch_warranty']) ? $meta['watch_warranty'] : 'no';
   489‚Üí        $prompt .= "SCATOLA ORIGINALE: " . $box . "\n";
