   290‚Üí    /**
   291‚Üí     * Gestisci richiesta AJAX per generare descrizione
   292‚Üí     */
   293‚Üí    public function handle_generate_description() {
   294‚Üí        // Verifica nonce
   295‚Üí        if (!wp_verify_nonce($_POST['nonce'], 'aiwd_generate_nonce')) {
   296‚Üí            wp_die('Nonce verification failed');
   297‚Üí        }
   298‚Üí        
   299‚Üí        $product_id = intval($_POST['product_id']);
   300‚Üí        $product = wc_get_product($product_id);
   301‚Üí        
   302‚Üí        if (!$product) {
   303‚Üí            wp_send_json_error(array('message' => 'Prodotto non trovato'));
   304‚Üí        }
   305‚Üí        
   306‚Üí        // Estrai informazioni dal prodotto
   307‚Üí        $product_data = $this->extract_product_data($product);
   308‚Üí        
   309‚Üí        // Genera descrizione con OpenAI
   310‚Üí        $description = $this->generate_description_with_openai($product_data);
   311‚Üí        
   312‚Üí        if (is_wp_error($description)) {
   313‚Üí            wp_send_json_error(array('message' => $description->get_error_message()));
   314‚Üí        }
   315‚Üí        
   316‚Üí        // Verifica unicit√†
   317‚Üí        if ($this->is_duplicate_description($description, $product_id)) {
   318‚Üí            // Rigenera con variazione
   319‚Üí            $description = $this->generate_description_with_openai($product_data, true);
   320‚Üí        }
   321‚Üí        
   322‚Üí        // Salva nel database
   323‚Üí        $this->save_generated_description($product_id, $description);
   324‚Üí        
   325‚Üí        wp_send_json_success(array('description' => $description));
   326‚Üí    }
   327‚Üí    
   328‚Üí    /**
   329‚Üí     * Estrai dati del prodotto
   330‚Üí     */
   331‚Üí    private function extract_product_data($product) {
   332‚Üí        $data = array(
   333‚Üí            'name' => $product->get_name(),
   334‚Üí            'sku' => $product->get_sku(),
   335‚Üí            'price' => $product->get_price(),
   336‚Üí            'short_description' => $product->get_short_description(),
   337‚Üí            'description' => $product->get_description(),
   338‚Üí            'attributes' => array(),
   339‚Üí            'categories' => array(),
   340‚Üí            'product_groups' => array()
   341‚Üí        );
   342‚Üí        
   343‚Üí        // Estrai categorie prodotto (per il brand)
   344‚Üí        $categories = wp_get_post_terms($product->get_id(), 'product_cat');
   345‚Üí        foreach ($categories as $category) {
   346‚Üí            $data['categories'][] = $category->name;
   347‚Üí        }
   348‚Üí        
   349‚Üí        // Estrai product_group (tassonomia specifica del sito)
   350‚Üí        $product_groups = wp_get_post_terms($product->get_id(), 'product_group');
   351‚Üí        foreach ($product_groups as $group) {
   352‚Üí            $data['product_groups'][] = $group->slug;
   353‚Üí        }
   354‚Üí        
   355‚Üí        // Estrai attributi
   356‚Üí        foreach ($product->get_attributes() as $attribute) {
   357‚Üí            $data['attributes'][$attribute->get_name()] = $attribute->get_options();
   358‚Üí        }
   359‚Üí        
   360‚Üí        // Estrai metadati personalizzati
   361‚Üí        // Meta keys reali ACF del sito Gioielleria Bonanno
   362‚Üí        $meta_keys = array('watch_reference', 'watch_production_year', 'watch_case', 'watch_dimension', 
   363‚Üí                          'watch_bracelet', 'watch_closing', 'watch_dial', 'watch_movement', 
   364‚Üí                          'watch_status', 'watch_box', 'watch_warranty', 'watch_seller_warranty', 
   365‚Üí                          'watch_notes', 'watch_private_negotiation', 'watch_availability', 'is_purchasable');
   366‚Üí        $data['meta'] = array(); // Inizializza array meta
   367‚Üí        foreach ($meta_keys as $key) {
   368‚Üí            $value = get_post_meta($product->get_id(), $key, true);
   369‚Üí            if ($value) {
   370‚Üí                $data['meta'][$key] = $value;
   371‚Üí            }
   372‚Üí        }
   373‚Üí        
   374‚Üí        // Estrai contenuto HTML della pagina
   375‚Üí        $data['page_content'] = $this->extract_page_content($product->get_id());
   376‚Üí        
   377‚Üí        return $data;
   378‚Üí    }
   379‚Üí    
   380‚Üí    /**
   381‚Üí     * Estrai contenuto della pagina
   382‚Üí     */
   383‚Üí    private function extract_page_content($product_id) {
   384‚Üí        $post = get_post($product_id);
   385‚Üí        $content = $post->post_content;
   386‚Üí        
   387‚Üí        // Rimuovi shortcode e HTML
   388‚Üí        $content = strip_shortcodes($content);
   389‚Üí        $content = wp_strip_all_tags($content);
   390‚Üí        
   391‚Üí        // Limita lunghezza
   392‚Üí        $content = substr($content, 0, 2000);
   393‚Üí        
   394‚Üí        return $content;
   395‚Üí    }
   396‚Üí    
   397‚Üí    /**
   398‚Üí     * Genera descrizione con OpenAI
   399‚Üí     */
   400‚Üí    private function generate_description_with_openai($product_data, $variation = false) {
   401‚Üí        $api_key = get_option('aiwd_openai_key');
   402‚Üí        if (!$api_key) {
   403‚Üí            return new WP_Error('no_api_key', 'Chiave API OpenAI non configurata');
   404‚Üí        }
   405‚Üí        
   406‚Üí        $prompt = $this->build_prompt($product_data, $variation);
   407‚Üí        
   408‚Üí        $response = wp_remote_post('https://api.openai.com/v1/chat/completions', array(
   409‚Üí            'headers' => array(
   410‚Üí                'Authorization' => 'Bearer ' . $api_key,
   411‚Üí                'Content-Type' => 'application/json'
   412‚Üí            ),
   413‚Üí            'body' => json_encode(array(
   414‚Üí                'model' => get_option('aiwd_model', 'gpt-3.5-turbo'),
   415‚Üí                'messages' => array(
   416‚Üí                    array(
   417‚Üí                        'role' => 'system',
   418‚Üí                        'content' => 'Sei un esperto di orologi di lusso che crea descrizioni concise e professionali per un sito di gioielleria.'
   419‚Üí                    ),
   420‚Üí                    array(
   421‚Üí                        'role' => 'user',
   422‚Üí                        'content' => $prompt
   423‚Üí                    )
   424‚Üí                ),
   425‚Üí                'max_tokens' => 150,
   426‚Üí                'temperature' => $variation ? 0.8 : 0.7
   427‚Üí            )),
   428‚Üí            'timeout' => 30
   429‚Üí        ));
   430‚Üí        
   431‚Üí        if (is_wp_error($response)) {
   432‚Üí            return $response;
   433‚Üí        }
   434‚Üí        
   435‚Üí        $body = wp_remote_retrieve_body($response);
   436‚Üí        $data = json_decode($body, true);
   437‚Üí        
   438‚Üí        if (isset($data['choices'][0]['message']['content'])) {
   439‚Üí            return trim($data['choices'][0]['message']['content']);
   440‚Üí        }
   441‚Üí        
   442‚Üí        return new WP_Error('api_error', 'Errore nella risposta API');
   443‚Üí    }
   444‚Üí    
   445‚Üí    /**
   446‚Üí     * Costruisci prompt per OpenAI
   447‚Üí     */
   448‚Üí    private function build_prompt($product_data, $variation = false) {
   449‚Üí        $prompt = "Crea una descrizione professionale di massimo 5 righe per questo orologio di lusso da gioielleria:\n\n";
   450‚Üí        
   451‚Üí        // Estrai brand dalla categoria prodotto
   452‚Üí        $brand = '';
   453‚Üí        if (!empty($product_data['categories'])) {
   454‚Üí            $brand = $product_data['categories'][0];
   455‚Üí        }
   456‚Üí        
   457‚Üí        $prompt .= "OROLOGIO: " . $product_data['name'] . "\n";
   458‚Üí        if ($brand) $prompt .= "MARCA: " . $brand . "\n";
   459‚Üí        
   460‚Üí        // Aggiungi categoria prodotto per personalizzare il linguaggio
   461‚Üí        if (!empty($product_data['product_groups'])) {
   462‚Üí            $group = $product_data['product_groups'][0];
   463‚Üí            $prompt .= "CATEGORIA: " . ucfirst($group) . "\n";
   464‚Üí            
   465‚Üí            // Personalizza approccio per categoria
   466‚Üí            if ($group === 'vintage') {
   467‚Üí                $prompt .= "STILE: Orologio d'epoca di valore storico\n";
   468‚Üí            } elseif ($group === 'premium') {
   469‚Üí                $prompt .= "STILE: Pezzo di alta orologeria di lusso\n";
   470‚Üí            } elseif ($group === 'accessories') {
   471‚Üí                $prompt .= "STILE: Accessorio per orologeria\n";
   472‚Üí            }
   473‚Üí        }
   474‚Üí        
   475‚Üí        // Campi specifici ACF del sito
   476‚Üí        $meta = $product_data['meta'];
   477‚Üí        if (!empty($meta['watch_reference'])) $prompt .= "REFERENZA: " . $meta['watch_reference'] . "\n";
   478‚Üí        if (!empty($meta['watch_production_year'])) $prompt .= "ANNO: " . $meta['watch_production_year'] . "\n";
   479‚Üí        if (!empty($meta['watch_case'])) $prompt .= "CASSA: " . $meta['watch_case'] . "\n";
   480‚Üí        if (!empty($meta['watch_dimension'])) $prompt .= "DIMENSIONI: " . $meta['watch_dimension'] . "mm\n";
   481‚Üí        if (!empty($meta['watch_movement'])) $prompt .= "MOVIMENTO: " . $meta['watch_movement'] . "\n";
   482‚Üí        if (!empty($meta['watch_dial'])) $prompt .= "QUADRANTE: " . $meta['watch_dial'] . "\n";
   483‚Üí        if (!empty($meta['watch_bracelet'])) $prompt .= "BRACCIALE: " . $meta['watch_bracelet'] . "\n";
   484‚Üí        if (!empty($meta['watch_status'])) $prompt .= "CONDIZIONI: " . $meta['watch_status'] . "\n";
   485‚Üí        
   486‚Üí        // Accessori e garanzia
